  }

  return (
    
    <Container>
    {console.log('sdsdf' + inputs.firstname)}
    <Form onSubmit={handleSubmit} style={{ width: '300px',  }}>
      <Form.Group className="mb-3">
        <Form.Label>First Name:</Form.Label>
        <Form.Control 
          type="text"
          name="firstname"
          value={inputs.firstname || props.initialValues.firstname || ""} 
          onChange={handleChange}
        />
      </Form.Group>

      <Form.Group className="mb-3">
        <Form.Label>Last Name:</Form.Label>
        <Form.Control 
          type="text" 
          name="lastname"
          value={inputs.lastname || props.initialValues.lastname || ""} 
          onChange={handleChange}
        />
      </Form.Group>

      <Form.Group className="mb-3">
        <Form.Label>Email:</Form.Label>
        <Form.Control 
          type="email" 
          name="email"
          value={inputs.email || props.initialValues.email || ""} 
          onChange={handleChange}
        />
      </Form.Group>

      <Form.Group className="mb-3">
        <Form.Label>Phone number:</Form.Label>
        <Form.Control 
          type="text"
          name="phonenumber"
          value={inputs.phonenumber || props.initialValues.phonenumber || ""} 
          onChange={handleChange}
        />
      </Form.Group>

      <Button variant="primary" type='submit'>Save</Button>
    </Form>
    </Container>
  )
}

export default UserForm;
