import React from "react";
import { Button } from "react-bootstrap";
import { Link } from "react-router-dom";
import axios from "axios";

const UserRow = ( props ) => {

  const { _id, firstname, lastname, email, phonenumber, profile } = props.obj;

  const deleteUser = async () => {

    const res = await axios.delete("http://localhost:4000/users/update-user/" + id, userObject);

    if (res.data.status === 200) {
      alert("User successfully updated");
      props.history.push("/user-list");
    }

    axios
      .delete(
        "http://localhost:4000/users/delete-user/" + _id)
        .then((res) => {
          if (res.status === 200) {
            alert("User successfully deleted");
            window.location.reload();
          } else Promise.reject();
      })
      .catch((err) => alert("Something went wrong"));
  };

  return (
    <tr>
      <td>{firstname}</td>
      <td>{lastname}</td>
      <td>{email}</td>
      <td>{phonenumber}</td>
      <td>{profile}</td>
      <td>
        <Link className="edit-link" to={"/users/update/" + _id}>Edit</Link>
        <Button onClick={deleteUser} size="sm" variant="danger">Delete</Button>
      </td>
    </tr>
  );
};

export default UserRow;
