const UserUpdate = () => {
  
  const onSubmit = ( userObject ) => {
    console.log( userObject );

    axios.post( 
        'http://localhost:4000/users/create-user', 
        userObject
      )
    .then(res => {
      if (res.status === 200)
        alert('Student successfully created')
      else
        Promise.reject()
    })
    .catch(err => alert('Something went wrong'))
  }

  return <>
    <UserForm 
      onSubmit={onSubmit}
    />
  </>;

};

export default UserUpdate;
