import { useState } from 'react';
import UserForm from './UserForm';
import axios from 'axios';

const CreateUser = ( props ) => {

  const [formValues, setFormValues] = useState({
    firstname: "",
    lastname: "",
    email: "",
    phonenumber: "",
  });

  const onSubmit = async ( userObject ) => {

    console.log( userObject.file );
    const formData = new FormData();
    formData.append("firstname", userObject.firstname);
    formData.append("lastname", userObject.lastname);
    formData.append("email", userObject.email);
    formData.append("phonenumber", userObject.phonenumber);
    formData.append("phonenumber", userObject.phonenumber);
    formData.append("profilefile", userObject.profilefile);
    
    try {

      const res = await axios({
        method: "post",
        url: "http://localhost:4000/users/create-usere",
        data: formData,
        headers: { "Content-Type": "multipart/form-data" },
      });

      //const res = await axios.post("http://localhost:4000/users/create-user", userObject);

      if (res.data.status === 201) {
        alert("User successfully created");
      }
    } catch( e ) {
      console.log(e);
    }
  }

  return <>
    <UserForm
      initialValues={formValues}
      onSubmit={onSubmit}
    />
  </>;
};

export default CreateUser;
