import { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { Container, Form, Button } from 'react-bootstrap';

const UserView = ( props ) => {

  const { id } = useParams();

  const [formValues, setFormValues] = useState({
    firstname: "",
    lastname: "",
    email: "",
    phonenumber: "",
  });

  useEffect( () => {

    async function fetchData() {
      try{
      const res = await axios.get("http://localhost:4000/users/update-user/" + id);

      const { firstname, lastname, email, phonenumber } = res.data;
      setFormValues({ firstname, lastname, email, phonenumber });

      } catch( err ) {
        console.error(err);
      }
    }

    fetchData();
  }, [id]);

  return <>
      
      <Container>
    <Form onSubmit={handleSubmit} style={{ width: '300px',  }}>
      <Form.Group className="mb-3">
        <Form.Label>First Name:</Form.Label>
        <Form.Control 
          type="text"
          name="firstname"
          value={formValues.firstname || ""} 
          onChange={handleChange}
        />
      </Form.Group>

      <Form.Group className="mb-3">
        <Form.Label>Last Name:</Form.Label>
        <Form.Control 
          type="text" 
          name="lastname"
          value={formValues.lastname || ""} 
        />
      </Form.Group>

      <Form.Group className="mb-3">
        <Form.Label>Email:</Form.Label>
        <Form.Control 
          type="email" 
          name="email"
          value={formValues.email || props.initialValues.email || ""} 
        />
      </Form.Group>

      <Form.Group className="mb-3">
        <Form.Label>Phone number:</Form.Label>
        <Form.Control 
          type="text"
          name="phonenumber"
          value={formValues.phonenumber || props.initialValues.phonenumber || ""} 
          onChange={handleChange}
        />
      </Form.Group>

      <Button variant="primary" type='submit'>Save</Button>
    </Form>
    </Container>

  </>;

};

export default UserView;
